$(document).ready(function () {
    $(".site-training .owl-carousel").owlCarousel({
        loop: true,
        autoplay: true,
        autoplayTimeout: 10000,
        dots: true,
        responsive: {
            0: {
                items: 1
            },
            576: {
                items: 2
            },
            768: {
                items: 1
            },
            992: {
                items: 2
            }
        }
    });
});

let navbar = $(".navbar");
let navLink = $(".nav-link");
let navBtn = $(".menu-right-btn");
let menuBurger = $(".fa-Otop");

$(window).scroll(function () {
    let oTop = $(".switch-navbar").offset().top - window.innerHeight;
    if($(window).scrollTop() > oTop) {
        navbar.addClass("sticky");
        navLink.addClass("color-nav-link");
        navBtn.addClass("color-nav-btn");
        menuBurger.addClass("color-menu-burger");
    } else {
        navbar.removeClass("sticky");
        navLink.removeClass("color-nav-link");
        navBtn.removeClass("color-nav-btn");
        menuBurger.removeClass("color-menu-burger");
    }
});

$(function(){ 
    var navMain = $(".navbar-collapse");
    navMain.on("click", "a:not([data-toggle])", null, function () {
        navMain.collapse('hide');
    });
});